#' mutFilterCOSMIC
#' @description Filter variants not in COSMIC
#'
#' @param maf An MAF data frame, generated by \code{\link{vcfToMAF}} function.
#' 
#' @return An MAF data frame after COSMIC filtering

mutFilterCOSMIC <- function(maf) {
  
    if ('Existing_variation' %in% colnames(maf)) {
    
      maf_filtered <- data.frame(maf[grep('COSV', maf[, 
                                                      'Existing_variation']), ])
    
      rownames(maf_filtered) <- 1:nrow(maf_filtered)
      return(maf_filtered)
    
  
  } else {
    return(maf)
  }
}
